

	var Class = require( "ee-class" );



	module.exports = new Class( {


		render: function( data, request, response, callback ){
			response.setHeader( "Content-Type", "text/html; charset=UTF-8" );
			callback( '<html><head><script src="https://google-code-prettify.googlecode.com/svn/loader/run_prettify.js"></script></head><body><pre class="prettyprint">' + JSON.stringify( data ) + '</pre></body></html>' );
		}

	} );